import { Avatar } from "@material-ui/core";
import React, { useState } from "react";
import { useEffect } from "react";
import { useDispatch } from "react-redux";
import { setChat } from "./features/messageSlice";
import db from "./firebase";
import "./SidebarChat.css";
//import * as timeago from "timeago.js";
// CHANGE SE
import axios from './axios'




function SidebarChat({ id, roomName }) {
    const dispatch = useDispatch();
    const [lastMessage, setLastMessage] = useState('')
    const [lastPic, setLastPic] = useState('')
    const [lastTime, setLastTime] = useState('')



    const getLastInfo = () => {
    axios.get(`/messages/menuData?id=${id}`).then((res) => {
      setLastMessage(res.data.message)
      setLastPic(res.data.user.pic)
      setLastTime(res.data.time)
    })
  }
    useEffect(()=>{
        getLastInfo();



        //real time ans pusher stuff
    } , [id]);

  return (
        <div onClick = {() => dispatch(
            setChat({
                messageId: id,
                roomName: roomName,
            })
        )} 
        className = "menuChat">
            <Avatar src = {lastPic}/>
            <div className="chat_section">
                <h2>{roomName}</h2>
                <p>{lastMessage}</p>
                <p>{new Date(parseInt(lastTime)).toUTCString()}</p>
            </div>
        </div>
    );
}

export default SidebarChat;
