import { Avatar, IconButton } from "@material-ui/core";
import React, { useEffect, useState } from "react";
import "./Sidebar.css";
import SearchIcon from "@material-ui/icons/Search";
import RateReviewOutlinedIcon from "@material-ui/icons/RateReviewOutlined";
import SidebarChat from "./SidebarChat";
import { useSelector } from "react-redux";
import { selectUser } from "./features/userSlice";
import db, { auth } from "./firebase";

import axios from './axios'



function Sidebar() {
  const user = useSelector(selectUser);
  const [messages, setMessages] = useState([]);

  const getRooms = () => {
        axios.get('/messages/messageList').then((res) =>{
            setMessages(res.data);
        })
    }

  const creatingRoom = (e) => {
        e.preventDefault()
        const roomName = prompt('Please enter a chat name')
        const firstMessage = prompt('Please send a welcome message')
        if (roomName && firstMessage) {
            let messageId = ''
            axios.post('/messages/room', {
            roomName: roomName
        }).then((res) => {
            messageId = res.data._id
      }).then(() => {
          axios.post(`/messages/new?id=${messageId}`, {
          message: firstMessage,
          time: Date.now(),
          user: user
        })
      })
    }
  }

  useEffect(() => {
    getRooms()
  }, []);

  return (
    <div className="sidebar">
      <div className="sidebar__header">
        <Avatar
          onClick={() => auth.signOut()}
          src={user.photo}
          className="sidebar__avatar"
        />
        <div className="sidebar__input">
          <SearchIcon />
          <input placeholder="Search" />
        </div>

        <IconButton variant="outlined" className="sidebar__inputButton">
          <RateReviewOutlinedIcon onClick={creatingRoom} />
        </IconButton>
      </div>

      <div className="sidebar__chats">
        {messages.map(({ id, name, time }) => (
          <SidebarChat key={id} id={id} roomName={name} time={time} />
        ))}
      </div>
    </div>
  );
}

export default Sidebar;
