import React from 'react'
import "./Menu.css"
import {IconButton} from "@material-ui/core"
import SearchIcon from "@material-ui/icons/Search";
import RateReviewOutlinedIcon from "@material-ui/icons/RateReviewOutlined";
import MenuChat from './MenuChat'
import { useSelector } from "react-redux";
import { selectUser } from "./redux/userSlice";
import db, { auth } from "./firebase";

import axios from './axios'



function Sidebar() {
  const user = useSelector(selectUser);
    // rooms, setRooms before
  const [messages, setMessages] = useState([]);

  const getRooms = () => {
    axios.get('/messages/messageList').then((res) =>{
      setMessages(res.data);
        })
    }
  const creatingRoom = (e) => {
    e.preventDefault()
    const roomName = prompt('Please enter a chat name')
    const firstMessage = prompt('Please send a welcome message')
    if (roomName && firstMessage) {
      let messageId = ''
      axios.post('/messages/room', {
        roomName: roomName
      }).then((res) => {
        messageId = res.data._id
      }).then(() => {
        axios.post(`/messages/new?id=${messageId}`, {
          message: firstMessage,
          time: Date.now(),
          user: user
        })
      })
    }
  }

    useEffect(()=>{
        getRooms()
        } , []);

  return (
        <div className="menu">
            <div className="menu_header">
                <IconButton>
                <CancelIcon
                onClick={() => auth.signOut()}
                className = "sign_out"/>
                </IconButton>
                <div className="menu_input">
                    <SearchIcon className = "search" />
                    <input className = "searchbar"  placeholder='Search' />
                </div>
                <IconButton variant = 'first' className = 'new_message_button'>
                    <RateReviewOutlinedIcon onClick = {creatingRoom}
                    />
                </IconButton>
            </div>
                <div className="menu_chats">
                    {messages.map(({id, name}) =>(
                    <SidebarChat key = {id} id = {id}  roomName = {name}  />
                    ))}
                </div>
        </div>

    ) 
}

export default Sidebar;
