import React, { useState, useEffect } from "react";
import "./MenuChat.css";
import { Avatar } from "@material-ui/core";
import { setRooms } from "../redux/messageSlice";
import { useDispatch } from "react-redux";
//import * as timeago from "timeago.js";
import Pusher from './'
import axios from '../axios'

function MenuChat({ id, roomName }) {
    const dispatch = useDispatch();
    const [lastMessage, setLastMessage] = useState('')
    const [lastPic, setLastPic] = useState('')



    const getLastInfo = () => {
    axios.get(`/messages/menuData?id=${id}`).then((res) => {
      setLastMessage(res.data.message)
      setLastPic(res.data.user.pic)
    })
  }
    useEffect(()=>{
        getLastInfo();



        //real time ans pusher stuff
    } , [id]);

  return (
        <div onClick = {() => dispatch(
            setRooms({
                messageId: id,
                roomName: roomName,
            })
        )} 
        className = "menuChat">
            <Avatar src = {lastPic}/>
            <div className="chat_section">
                <h2>{roomName}</h2>
                <p>{lastMessage}</p>

            </div>
        </div>
    );
}

export default MenuChat;
