import { Avatar, IconButton } from "@material-ui/core";
import React, { useEffect, useState } from "react";
import "./Sidebar.css";
import SearchIcon from "@material-ui/icons/Search";
import RateReviewOutlinedIcon from "@material-ui/icons/RateReviewOutlined";
import SidebarChat from "./SidebarChat";
import { useSelector } from "react-redux";
import { selectUser } from "./features/userSlice";
import db, { auth } from "./firebase";

import axios from './axios'



function Sidebar() {
  const user = useSelector(selectUser);
  const [chats, setChats] = useState([]);

  const getChats = () => {
    axios.get('/messages/messageList')
      .then((res) => {
        setChats(res.data)
      })
  }

  useEffect(() => {
    getChats()
  }, []);

  const addChat = (e) => {
    e.preventDefault()

    const roomName = prompt('Please enter a chat name')
    const firstMsg = prompt('Please send a welcome message')

    if (roomName && firstMsg) {
      let chatId = ''

      axios.post('/messages/room', {
        roomName: roomName
      }).then((res) => {
        chatId = res.data._id
      }).then(() => {
        axios.post(`/messages/new?id=${chatId}`, {
          message: firstMsg,
          time: Date.now(),
          user: user
        })
      })
    }
  }

  return (
    <div className="sidebar">
      <div className="sidebar__header">
        <Avatar
          onClick={() => auth.signOut()}
          src={user.photo}
          className="sidebar__avatar"
        />
        <div className="sidebar__input">
          <SearchIcon />
          <input placeholder="Search" />
        </div>

        <IconButton variant="outlined" className="sidebar__inputButton">
          <RateReviewOutlinedIcon onClick={addChat} />
        </IconButton>
      </div>

      <div className="sidebar__chats">
        {chats.map(({ id, name, time }) => (
          <SidebarChat key={id} id={id} roomName={name} time={time} />
        ))}
      </div>
    </div>
  );
}

export default Sidebar;
